<?php include_once "../includes/header.php"; if (!$_GET['pid']) {	$pid = '1';} else {	$pid = preg_replace("[^0-9]", "" ,  $_GET['pid']);}$sqlCommand = "SELECT pagetitle, id, pagebody FROM pages WHERE id='$pid' LIMIT 1"; $query = mysqli_query($myConnection, $sqlCommand) or die (mysqli_error()); while ($row = mysqli_fetch_array($query)) {     $pagetitle = $row["pagetitle"];	$pagebody = $row["pagebody"];	$pagebody = str_replace("<br />", "", $pagebody);} mysqli_free_result($query); ?>On this page you can edit the content of the page.<hr><form id="form" name="form" method="post" action="edit_page_parse.php"><h3>Pagetitle</h3><input class="form-control" name="pagetitle" type="text" value="<?php echo $pagetitle; ?>"/><br><hr><h3>Pagebody</h3><textarea name="contentedit"><?php echo $pagebody ?></textarea><hr><input name="pid" type="hidden" value="<?php echo $pid; ?>" /><input type="submit" name="button" id="button" value="Update page" /></form><?php include_once "../includes/footer.php"; ?>