<?php// Start a session //session_start();// Check if the user is logged in //if(!isset($_SESSION['logged_in']) || $_SESSION['logged_in'] == false){    header('Location: ../login_form.php');    exit();}// Show the welcometext above the page //echo '<center><font color=lightblue size=5>Welcome '.$_SESSION['username'].' nice to see you</font></center>';// ----------------------------------------------------------------------- //if (!$_GET['pid']) {	$pid = '1';} else {	$pid = preg_replace("[^0-9]", "" ,  $_GET['pid']);}// Query the body section for the page //include_once "../../scripts/connect_to_mysql.php";$sqlCommand = "SELECT pagetitle, id, pagebody FROM pages WHERE id='$pid' LIMIT 1"; $query = mysqli_query($myConnection, $sqlCommand) or die (mysqli_error()); while ($row = mysqli_fetch_array($query)) {     $pagetitle = $row["pagetitle"];	$pagebody = $row["pagebody"];	$pagebody = str_replace("<br />", "", $pagebody);} mysqli_free_result($query); ?><!DOCTYPE html><html><head><title>Leasy CMS - Admin</title><link href="../style/style.css" type="text/css" rel="stylesheet"><script type="text/javascript" src="../editor/ckeditor.js"></script></head><body><center><?php require_once("../menu.php"); ?><section id="content"><article><h2>Leasy CMS</h2>On this page you can edit the content of the page.</article><hr><article><form id="form" name="form" method="post" action="edit_page_parse.php"><h1>Pagetitle</h1><input name="pagetitle" type="text" value="<?php echo $pagetitle; ?>"/><br><hr><h1>Pagebody</h1><textarea name="pagebody"><?php echo $pagebody ?></textarea><script type="text/javascript">CKEDITOR.replace( 'pagebody' );</script><hr><input name="pid" type="hidden" value="<?php echo $pid; ?>" /><input type="submit" name="button" id="button" value="Update page" /></form></article><hr><article>Leasy CMS is a product of <a href="http://www.abakay.zxq.net" target="_blank">Ahmed Bakay</a></article></section></center></body></html>