<?php include_once "../includes/header.php"; if (!$_GET['pid']) {	$pid = '1';} else {	$pid = preg_replace("[^0-9]", "" ,  $_GET['pid']);}$sqlCommand = "SELECT pagetitle, id, pagebody FROM pages WHERE id='$pid' LIMIT 1"; $query = mysqli_query($myConnection, $sqlCommand) or die (mysqli_error()); while ($row = mysqli_fetch_array($query)) {     $pagetitle = $row["pagetitle"];	$pagebody = $row["pagebody"];	$pagebody = str_replace("<br />", "", $pagebody);} mysqli_free_result($query); ?><section id="content"><article><h2>Leasy CMS</h2>On this page you can edit the content of the page.</article><hr><article><form id="form" name="form" method="post" action="edit_page_parse.php"><h1>Pagetitle</h1><input name="pagetitle" type="text" value="<?php echo $pagetitle; ?>"/><br><hr><h1>Pagebody</h1><textarea name="contentedit"><?php echo $pagebody ?></textarea><hr><input name="pid" type="hidden" value="<?php echo $pid; ?>" /><input type="submit" name="button" id="button" value="Update page" /></form></article><hr><?php include_once "../includes/footer.php"; ?>