<?php include_once "../includes/header.php"; ?>Click on the pagetitle to edit a page.<hr><?phprequire_once "../../scripts/connect_to_mysql.php";$sqlCommand = "SELECT id, pagetitle FROM pages ORDER BY id ASC"; $query = mysqli_query($myConnection, $sqlCommand) or die (mysqli_error()); while ($row = mysqli_fetch_array($query)) {     $id = $row["id"];    $pagetitle = $row["pagetitle"];	$pagedisplay = "<a href=edit_page.php?pid=$id>$pagetitle</a><br>";	echo $pagedisplay;} mysqli_free_result($query); ?><?php include_once "../includes/footer.php"; ?>